SELECT str_to_date(DATE_FORMAT(time,'%Y%m%d'),'%Y%m%d') day,count(*) value from 
(SELECT * from park_info WHERE time >=  #{0} and time  <=  #{1}) as b  
GROUP BY day ORDER BY day;

